interface ICoin {
    id: string,
    name: string,
    symbol: string,
    rank: number,
    is_new: boolean,
    is_active: boolean,
    type: string,
}


const Coins = () =>{    


    const {isLoading, data }  = useQuery<ICoin[]>("allCoins",fetchCoins)
    // const [coins,setCoins] = useState<CoinInterface[]>([]);
    // const [loding, setLoding] = useState(true)
    // useEffect(()=>{
