    return (
        <Container>
            <Header>
                <Title>코인</Title>
            </Header>

 { isLoading?(<Loding> loading... </Loding>)    :    ( <CoinList>
                {data?.slice(1,100).map((coin)=>
                      <Coin key={coin.id}> 
                      <Link to={{
                          pathname:`/${coin.id}`
                          ,state:{
                              name:coin.name,
                          }
                      }}>
                          <Img src={`https://cryptoicon-api.vercel.app/api/icon/${coin.symbol.toLocaleLowerCase()}`}/> 
                         {coin.name}  
                      </Link>
                      </Coin> )}
            </CoinList>)}
        </Container>

    );
}
export default Coins;
