interface CoinInterface {
    id: string,
    name: string,
    symbol: string,
    rank: number,
    is_new: boolean,
    is_active: boolean,
    type: string,
}


const Coins = () =>{    
    const [coins,setCoins] = useState<CoinInterface[]>([]);
    const [loding, setLoding] = useState(true)

    useEffect(()=>{
        (async()=>{
            const response = await fetch("https://api.coinpaprika.com/v1/coins");
            const json = await response.json()
            setCoins(json.slice(0,100))
            setLoding(false)


        })();
    },[]);
    return (
        <Container>
            <Header>
                <Title>코인</Title>
            </Header>

 { loding?(<Loding> loading... </Loding>)    :    ( <CoinList>
                {coins.map(coin=>  <Coin key={coin.id}> <Img src={`https://cryptoicon-api.vercel.app/api/icon/${coin.symbol.toLocaleLowerCase()}`}/><Link to={`/${coin.id}`}>{coin.name}  </Link></Coin> )}
            </CoinList>)}
        </Container>

    );
}
export default Coins;
